app.provider('userInfo', function () {
    var mainUrl;
    //this.setting = function (base) {
    //    mainUrl = base.url;
    //};

    var obj = {};


    this.$get = ['$http', function ($http) {


        //user save service
        obj.userSave = function (success, error, scope) {
            var res = $http.post('user/save', scope);
            res.success(function (resp) {
                success(resp);
            });
            res.error(function (data, status) {
                error(status);
            });
        };

        ///change Password service

        obj.updatePassword = function (success, error, scope) {
            var res = $http.post('user/changePassword', {
                'userName': scope.userName,
                'oldPassword': scope.oldPassword,
                'newPassword': scope.newPassword
            });
            res.success(function (resp) {
                success(resp);
            });
            res.error(function (data, status) {
                error(status);
            });
        };


        //load list of user info

        //obj.userList = function (success, error, scope) {
        //    var res = $http.post('user/userList',scope);
        //    res.success(function (resp) {
        //        success(resp);
        //    });
        //    res.error(function (data, status, headers, config) {
        //        error(status);
        //    });
        //};


        obj.userList = function (success, error, scope) {
            var res = $http.get('user/userList',scope);
            res.then(res.success(function (resp) {
                success(resp);
            }),
            res.error(function (data, status) {
                error(status);
            }));

        };


        //updateUser


        //user update service
        obj.updateUser = function (success, error, scope) {
            var res = $http.post('user/update', scope);
            res.success(function (resp) {
                success(resp);
            });
            res.error(function (data, status) {
                error(status);
            });
        };



        //Delete User


        obj.deleteUser = function (success, error, scope) {
            var res = $http.post('user/delete', scope.id);
            res.success(function (resp) {
                success(resp);
            });
            res.error(function (data, status) {
                error(status);
            });
        };



        //Customer save service
        obj.customerSave = function (success, error, scope) {
            var res = $http.post('customer/save', scope);
            res.success(function (resp) {
                success(resp);
            });
            res.error(function (data, status) {
                error(status);
            });
        };



        //get Customer List
        obj.customerList = function (success, error) {
            var res = $http.get('customer/customerList');
            res.then(res.success(function (resp) {
                    success(resp);
                }),
                res.error(function (data, status) {
                    error(status);
                }));

        };


        //user update service
        obj.updateCustomer = function (success, error, scope) {
            var res = $http.post('customer/update', scope);
            res.success(function (resp) {
                success(resp);
            });
            res.error(function (data, status) {
                error(status);
            });
        };



        //Delete Customer


        obj.deleteCustomer = function (success, error, scope) {
            var res = $http.post('customer/delete', scope.id);
            res.success(function (resp) {
                success(resp);
            });
            res.error(function (data, status) {
                error(status);
            });
        };



        return obj;
    }];





});


