app.service('userDetails', ['$http', '$location', function ($http, $location) {
    this.principal = function (x) {
        $http({
            url:'/user/principle',
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(function (resp) {
            x(resp);
        }, function (err) {
        });
    };

}]);


