//Function for update User controller

function convertRoleToUserType(role) {
    var userType = '';
    switch (role) {
        case 'ROLE_ADMIN':
            userType = 'Admin';
            break;
        case 'ROLE_USER':
            userType = 'User';
            break;
        case 'ROLE_SUPER_ADMIN':
            userType = 'Super Admin';
            break;
        case 'ROLE_OTHER':
            userType = 'Other';
            break;
        default :
            userType = 'User';
    }
    return userType;

}


